  formatDates() {
    this.dates.arrival = this.handlers.getDayName_dd_Mon_YYYY(this.sharedData.travel.searchParameters.arrivalDate[this.sharedData.travel.searchParameters.arrivalDate.length - 1], true);
    this.dates.departure = this.handlers.getDayName_dd_Mon_YYYY(this.sharedData.travel.searchParameters.departureDate[0], true);
  }




  flightDuration(origin_destination) {
    const length = origin_destination.segments.length;
    const dep = origin_destination.segments[0].departure.date + "T" + origin_destination.segments[0].departure.time;
    const arr = origin_destination.segments[length - 1].arrival.date + "T" + origin_destination.segments[length - 1].arrival.time;

    const millis = new Date(Date.parse(arr) - Date.parse(dep));

    const x = this.handlers.msToTime(millis);

    let duration = x.split(':');
    // const hours = `${duration[0]} hrs`;
    // const minutes = `${duration[1]} mins`;
    let hours, minutes;

    // Format hour
    if (parseInt(duration[0]) < 1) {
      hours = "";
    }
    else if (parseInt(duration[0]) === 1) {
      hours = `${duration[0]} hr`;
    }
    else {
      hours = `${duration[0]} hrs`;
    }

    // Format minute
    if (parseInt(duration[1]) < 1) {
      minutes = "";
    }
    else if (parseInt(duration[1]) === 1) {
      minutes = `${duration[1]} min`;
    }
    else {
      minutes = `${duration[1]} mins`;
    }

    return `${hours} ${minutes}`;
  }



