  numberOfStops(origin_destination) {
    const length = origin_destination.segments.length
    if (length === 1) {
      return "Non-stop";
    }
    else if (length > 1) {
      if (length === 2) {
        return "1 stop";
      }
      else {
        return (`${length - 1} stops`);
      }
    }
    else {
      return ""
    }
  }

