  showTicketDetails(itinerary) {
    this.handlers.presentSwipeableModal('flight-ticket-details', FlightTicketDetailsComponent);
    this.sharedData.travel.ticketDetails = itinerary;
  }

  sort() {
    this.handlers.presentPopover(SortComponent)
  }

  truncateResults(itineraries) {
    let truncatedItineraries = [];
    if (itineraries.length > 10) {
      const length = itineraries.length;
      for (let i = 0; i < length; i += 10) {
        const x = itineraries.slice(i, i + 10);
        truncatedItineraries.push(x);
      }
    }
    this.sharedData.travel.truncated.allItineraries = JSON.parse(JSON.stringify(truncatedItineraries));
    this.sharedData.travel.truncated.length = truncatedItineraries.length;
    this.sharedData.travel.truncated.max = truncatedItineraries.length - 1;
    this.sharedData.travel.truncated.view = 0;
  }

  changeView() {

    const action = {
      next: () => {
        if (this.sharedData.travel.truncated.view < this.sharedData.travel.truncated.max) {
          this.ionContent.scrollToTop(800);
          setTimeout(
            () => {
              this.sharedData.travel.truncated.view++;
            }, 700
          )
        }
      },
      previous: () => {
        if (this.sharedData.travel.truncated.view > 0) {
          this.ionContent.scrollToTop(800);
          setTimeout(
            () => {
              this.sharedData.travel.truncated.view--;
            }, 700
          )
        }
      },
      scrollToTop: () => {
        this.ionContent.scrollToTop(800);
      }
    }
    return action
  }

